import { FaFacebook, FaLinkedin } from "react-icons/fa";
import Enquiry from "./Enquiry";

const iframe =
  '<iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d971.4958516037842!2d80.20358952121363!3d13.100237685013816!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3a526576cd3c5eb9%3A0xe2ded01e55cf55fe!2sIndelect%20Technologies%20Pvt%20Limited!5e0!3m2!1sen!2sin!4v1631190591514!5m2!1sen!2sin" width="600" height="450" style="border:0;" allowfullscreen="" loading="lazy"></iframe>';

const Iframe = (props) => {
  return (
    <div
      dangerouslySetInnerHTML={{ __html: props.iframe ? props.iframe : "" }}
    />
  );
};

const Contact = () => {
  return (
    <div>
      <div class="flex-container">
        {/* <form
          id="contact-form"
          class="contact-form"
          onSubmit={callHandler}
          //method="POST"
        >
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input type="text" className="form-control" />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input
              type="email"
              className="form-control"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea className="form-control" rows="5"></textarea>
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form> */}
        <Enquiry />

        <div class="google-maps">
          <Iframe iframe={iframe} />
        </div>
        <div class="address-container ">
          <p class="paragraph">Indelect Technologies Pvt Limited</p>
          <p class="address-text">
            #15/53, 1st Floor, Thirunagar, Villivakkam, Chennai-600049.
          </p>
          <p class="address-text">Sales: +91-9380243402 </p>
          <p class="address-text">Services:+91-9382543299 </p>
          <p class="address-text">Email: Sales@indelect.com </p>
          <div class="social-media">
            <a href="https://www.facebook.com/pages/category/Community/Indelect-TechnologiesPvtLtd-144370088984560/">
