import { React } from "react";
import emailjs from "emailjs-com";

const Enquiry = () => {
  function sendEmail(e) {
    e.preventDefault();
    //console.log('Clicked Send');
    const btn = document.getElementById('button');
    btn.value = 'Sending...';
    emailjs
      .sendForm("indect-web-enquiry", "template_qq8g54v", e.target, "user_QSog1g5jxVpfjLGuagGzF")
      .then(
        (result) => {
          console.log(result.text);
          btn.value = 'Enquiry Sent!';
        },
        (error) => {
          console.log(error.text);
          btn.value = 'Send Email';
        }
      );
  }

  return (
    <div class="contact-form">
      <div class="form-heading">
        Send an enquiry
      </div>
      <form onSubmit={sendEmail}>
        <div class="mb-3 row">
          <label>Name</label>
          <input type="text" name="from_name" id="from_name" />
        </div>
        <div class="mb-3 row">
          <label>Email Address</label>
          <input type="text" name="from_email" id="from_email" placeholder="name@example.com" />
        </div>
        <div class="mb-3 row">
          <label>Message</label>
          <textarea name="message" id="message" />
        </div>
        <div className="btn-primary">
          <input type="submit" id="button" value="Send Enquiry"/>
        </div>  
      </form>
    </div>
  );
};

export default Enquiry;
