import { React, useState } from "react";
import data from "./data";
import { FaChevronRight, FaQuoteRight, FaChevronLeft } from "react-icons/fa";

const Feedback = () => {
    const [index, setIndex] = useState(0);
    const {name, position, company, location, info} = data[index];

    const nextPerson = () => {
        setIndex((index) => {
        let nextIndex = index + 1;
        return checkNumber(nextIndex);
        });
    };

    const prevPerson = () => {
        setIndex((index) => {
        let prevIndex = index - 1;   
        return checkNumber(prevIndex);
        });
    };

    const checkNumber = (number) => {
        if (number > data.length - 1) {
            return 0
        }
        if (number < 0) {
            return data.length - 1
        }
        return number;
    }



  return (
         <article className="review">
            <div>
            <span className='quote-icon'>
                <FaQuoteRight />
            </span>
           </div>
         <h4 className="name">{name}</h4> 
         <h4 className="position">{position}</h4> 
         <h4 className="company">{company}</h4> 
         <h4 className="location">{location}</h4>
         <h4 className="info">{info}</h4>
         <div className="button-container">
             <button className='prev-btn' onClick={prevPerson}>
                 <FaChevronLeft />
             </button>
             <button className='next-btn' onClick={nextPerson}>
                 <FaChevronRight />
             </button>
         </div>


         </article>
          );
};

  export default Feedback;
