import { React } from "react";
import emailjs from "emailjs-com";

const Enquiry = () => {
  function sendEmail(e) {
    e.preventDefault();
    //console.log('Clicked Send');
    const btn = document.getElementById('button');
    btn.value = 'Sending...';
    emailjs
      .sendForm("indect-web-enquiry", "template_qq8g54v", e.target, "user_QSog1g5jxVpfjLGuagGzF")
      .then(
        (result) => {
          console.log(result.text);
          btn.value = 'Enquiry Sent!';
        },
        (error) => {
          console.log(error.text);
          btn.value = 'Send Email';
        }
      );
  }

  return (
    <div className="form-group">
      <form onSubmit={sendEmail}>
        <div calss="mb-3">
          <label>Name</label>
          <input type="text" name="from_name" id="from_name" />
        </div>
        <div className="form-group">
          <label>Email Address</label>
          <input type="text" className="form-control" name="from_email" id="from_email" />
        </div>
        <div className="form-group">
          <label>Message</label>
          <textarea name="message" id="message" />
        </div>
        <div className="btn">
          <input type="submit" id="button" value="Send Enquiry"/>
        </div>  
      </form>
    </div>
  );
};

export default Enquiry;
