import React from "react";
import { FaQuoteLeft, FaQuoteRight } from "react-icons/fa";
import data from "../data/feedback-data";

const Customers = () => {
  return (
    <React.Fragment>
      <p class="main-heading">Customer Feedback</p>
      <p class="paragraph">
        Within a short span of time, Indelect as an organization has had the
        great privilege of working with a number of prominent organizations and
        individuals. With an enormous base of clientele, growing at a sound
        pace, Indelect aspires to build long lasting ties by providing world
        class services and solutions
      </p>
      {data.map((item, index) => {
        return (
          <React.Fragment key={index}>
            <div class="container space-around">
              <div class="card bg-light mb-2 card-style">
                <div class="card-header">
                  {item.name}, {item.position}, {item.company}, {item.location}
                </div>
                <div class='quote-container'>
                  <FaQuoteLeft />
                </div>
                <h5 class="card-body">{item.info}</h5>
                <div class="class='quote-container">
                  <FaQuoteRight />
                </div>  
              </div>
            </div>
          </React.Fragment>
        );
      })}
      ;
    </React.Fragment>
  );
};

export default Customers;
